ss/getList',array($jxw,$jl));  //精选        $lw['is_tuijian'] = 1;        $cityinfo = $this->cityinfo['ad_info'];        $lw['city_id'] = M("city")->where(array('city'=>array("like","%{$cityinfo['city']}%")))->getField('cityids');//        dump(M('city')->_sql());die;        $p = intval($_REQUEST['page']);        $psize = intval($_REQUEST['psize']);        $list = R('Func/Business/getList',array($lw,$jl,$p,$psize));        # 轮播图        $lbarr = R('Func/Func/getLunbo',array(2));        #附近icon        $banner = M('industry')->field('industry_id,icon,name')->where(array('type'=>1,'status'=>1))->order('sort asc')->select();//        foreach ($jxlist as $k=>$v){//            unset($jxlist['coupon']);//        }//        foreach ($list as $k=>$v){//            unset($list['coupon']);//        }//        $sheng=M('province')->where(array('provids'=>$data['province_id']))->getField('province');//        $shi=M('city')->where(array('cityids'=>$data['city_id']))->getField('city');//        $qu=M('area')->where(array('areaids'=>$data['area_id']))->getField('area');//        $diQuName=$sheng.$shi.$qu.$data['address'];        foreach ($jxlist as $k =>$v){            $jxlist[$k]['thumb']=enThumb('./Uploads/',$v['thumb']);            unset($jxlist[$k]['coupon']);        }        foreach ($list as $k =>$v){            $list[$k]['thumb']=enThumb('./Uploads/',$v['thumb']);            unset($list[$k]['coupon']);        }//        dump($jxlist);//        unset($jxlist['coupon']);//        unset($list['coupon']);        $d = array(            'code' => 2001,            'msg' => '商家首页',            'data' => array(                'city' => $this->cityinfo,                'lbarr' => $lbarr,  //轮播图                'banner'=>$banner, //icon//                'cate' => $cate,                'jxlist' => $jxlist,    //jx                'list' => $list  // tj            )        );        echo json_encode($d);exit;    }    public function getBusinesslist()    {               if (!$this->sign){                        echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;                    }                $msg = R('Func/Func/getKey',array($this->parem));//返回加密                //        dump($msg);die;                if ($this->sign !== $msg){                        echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;                    }                R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)                $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断                          $industry_id=$_REQUEST['industry_id'];                $keword=$_REQUEST['keword'];                 $lat=$_REQUEST['lat'];        $lng=$_REQUEST['lng'];        $page = $_REQUEST['page'];  //页数        if(!$lat)        {            $lat= $this->cityinfo["location"]['lat'];        }                if(!$lng)        {            $lng= $this->cityinfo["location"]['lng'];        }        $cityinfo = $this->cityinfo['ad_info'];        $where['city_id'] = M("city")->where(array('city'=>array("like","%{$cityinfo['city']}%")))->getField('cityid');        if($keword)        {            $where['_string']=" nick_name like '%$keword%' or phone like '%$keword%' or  name like '%$keword%' or address like '%$keword%' or shop_name like '%$keword%' ";        }//           var_dump($this->cityinfo);        if(!$page)        {            $page=1;        }                $pagenum=15;        $startnum=($page-1)*$pagenum;        $endnum=$page*$pagenum;                $limit=''.$startnum.','.$endnum.'';        //         $where['_string']=" industry_id=".$industry_id;        if($industry_id)        {            $where['industry_id']=$industry_id;        }        $where['status']=2;//         echo $lat.",".$lng;        if($where){            $businesslist=M('business')->where($where)->field('y_business.*,ROUND(6378.138*2*ASIN(SQRT(POW(SIN(('.$lat.'*PI()/180-y_business.lat*PI()/180)/2),2)+COS('.$lat.'*PI()/180)*COS(y_business.lat*PI()/180)*POW(SIN(('.$lng.'*PI()/180-y_business.lng*PI()/180)/2),2)))*1000) AS juli')->limit($limit)->order('juli DESC')->select();                    }else{            $businesslist=M('business')->field('y_business.*,ROUND(6378.138*2*ASIN(SQRT(POW(SIN(('.$lat.'*PI()/180-y_business.lat*PI()/180)/2),2)+COS('.$lat.'*PI()/180)*COS(y_business.lat*PI()/180)*POW(SIN(('.$lng.'*PI()/180-y_business.lng*PI()/180)/2),2)))*1000) AS juli')->limit($limit)->order('juli DESC')->select();                    }      //         echo M('business')->getLastSql();                for ($i = 0; $i < count($businesslist); $i++) {            $businesslist[$i]['thumb']=enThumb("./Uploads/",$businesslist[$i]['thumb']);            $businesslist[$i]['zhizhaoPic']=enThumb("./Uploads/",$businesslist[$i]['zhizhaoPic']);            $businesslist[$i]['goodsPic']=enThumb("./Uploads/",$businesslist[$i]['goodsPic']);        }                echo json_encode(array('code'=>200,'msg'=>'获取成功','businesslist'=>$businesslist));die;                   }    # 详情    public function getDetail()    {        if (!$this->sign){            echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;        }        $array=array(            'id'=>intval($_REQUEST['id']),        );        $this->parem = array_merge($this->parem,$array);        $msg = R('Func/Func/getKey',array($this->parem));//返回加密//        dump($msg);die;        if ($this->sign !== $msg){            echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;        }        R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)        $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断            $w['business_id'] = intval($_REQUEST['id']);            $loc = $this->cityinfo['location'];            $jl['lat1'] = $loc['lat'];            $jl['lng1'] = $loc['lng'];            $d = R('Func/Business/getList',array($w,$jl));            if(!$d)            {                $d = array(                    'code' => 400,                    'msg' => '没有这个参数值'                );                echo json_encode($d);exit;            }            # 猜你喜欢            $tj['cate'] = $d[0]['city_id'];            $tj['business_id'] = array('neq',$d[0]['business_id']);            $cityinfo = $this->cityinfo['ad_info'];            $tj['city_id'] = M("city")->where(array('name'=>$cityinfo['city']))->getField('cityids');            $tjdata = R('Func/Business/getList',array($tj,$jl,1,5));            $tjsjdata = shuffle_assoc($tjdata);            $this->assign('d',$d[0]);            $this->assign('tjsjdata',$tjsjdata);        $d = array(            'code' => 2002,            'msg' => '商家详情',            'data' => array(                'detail' => $d[0],                'tjsjdata' => $tjsjdata  // 相关推荐            )        );        echo json_encode($d);exit;    }    # 领取优惠券    public function ajaxLqCoupon()    {        if (!$this->sign){            echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;        }        $array=array(            'uid'=>$this->uid,            'couponid'=>intval($_REQUEST['couponid']),            'bid'=>intval($_REQUEST['bid']),        );        $this->parem = array_merge($this->parem,$array);        $msg = R('Func/Func/getKey',array($this->parem));//返回加密//        dump($msg);die;        if ($this->sign !== $msg){            echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;        }        R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)        $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断        $w['coupon_id'] = intval($_REQUEST['couponid']);        $w['business_id'] = intval($_REQUEST['bid']);        if(empty($w['coupon_id']) && empty($w['business_id']))        {            $d = array(                'code'=>200,                'msg'=>'参数不全'            );            echo json_encode($d);exit;        }        $w['user_id'] = $this->uid;        $yhqarr['coupon_id'] = intval($_REQUEST['couponid']);        $yhqcount = R("Func/Business/getCoupon",array($w['business_id'],$yhqarr));        $count = R("Func/Business/getLqCoupon",array($w));        $lq_num = $yhqcount[0]['lq_num'];        # 判断每个人针对优惠券是否可以领取多张        if($count<=0)        {            if(empty($lq_num))            {                $d = array(                    'code'=>200,                    'msg'=>'请商家设置每人领取多少张'                );                echoAJson($d);exit;            }            if($count>$lq_num)            {                $d = array(                    'code'=>200,                    'msg'=>'已超出领取次数'                );            }else{                # 添加领取数据//                $data['openid'] = trim($this->openid);                $data['coupon_id'] = intval($_REQUEST['couponid']);                $data['gettime'] = time();                $data['used'] = 0;                $data['user_id'] = $this->uid;                $data['business_id'] = intval($_REQUEST['bid']);                M('coupon_data')->add($data);                $d = array(                    'code'=>400,                    'msg'=>'领取成功'                );            }        }else{            $d = array(                'code'=>700,                'msg'=>'已领取'            );        }        echoAJson($d);exit;    }    #商家 收款记录(本月)    public function receiptList(){        $uid = $_REQUEST['business_id'];  //商家ID//        if (!$this->sign){            echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;        }        $array = array(            'business_id'=>$uid,        );        $this->parem=array_merge($this->parem,$array);        $msg = R('Func/Func/getKey',array($this->parem));//返回加密        if ($this->sign !== $msg){            echo json_encode(array('code'=>10004,'msg'=>'网络错误,请重新提交'));die;        }        R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)        $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断        if (!$uid){            echo json_encode(array('code'=>6502,'msg'=>'参数不全'));die;        }        $p=$_REQUEST['page'];        if (!$p){            $p=0;        }        #订单总数        $data = R('Func/Business/orderList',array($uid,$p));        $count = R('Func/Business/orderSum',array($uid));        //商家营业总额        $rental=R('Func/Business/rental',array($uid));        $rentalMoney=sprintf("%.2f",$rental);        $counts=count($count);        $sum=ceil($counts/5);//        echo json_encode(array('code'=>6501,'msg'=>'获取成功','data'=>$details,'pagesize'=>$sum,'pagesum'=>$pagesum));die;        echo json_encode(array('code'=>6501,'msg'=>'获取成功','data'=>$data,'pagesize'=>$count,'pagesum'=>$sum,'rental'=>$rentalMoney));        die;    }    #商家 收款记录(指定月份)    public function selectReceipt(){        $sj =$_REQUEST['time'];        $uid = $_REQUEST['business_id'];        if (!$this->sign){            echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;        }        $array = array(            'business_id'=>$uid,            'time'=>$_REQUEST['time'],        );        $this->parem=array_merge($this->parem,$array);        $msg = R('Func/Func/getKey',array($this->parem));//返回加密        if ($this->sign !== $msg){            echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;        }        R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)        $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断        if (!$uid || !$sj){            echo json_encode(array('code'=>6502,'msg'=>'参数不全'));die;        }        $p=$_REQUEST['page'];        if (!$p){            $p=0;        }        $num=15;        if (!$sj){            echo json_encode(array('code'=>6901,'msg'=>'日期参数不存在'));die;        }        $zhidingY=$sj.'-01 00:00:00';        $strY=substr($sj,0,4).'-';        $zhidingM=strtotime($zhidingY);        $endday = strtotime(date($strY.'m-d', mktime(23, 59, 59, date('m', strtotime($zhidingY))+1, 00)));        $endday=$endday+86399;//        dump($endday);die;        $w['business_id']=$uid;        $w['t']=array(array('egt',$zhidingM),array('elt',$endday));        $benyue=array();        $count =M('business_order')->alias('a')->field('a.*,b.money_type_id as b_id,b.type as b_type')->join('left join y_money_type as b on a.money_type_id = b.money_type_id')->where($w)->order('t desc')->count();        $sum=ceil($count/$num);        $data =M('business_order')->alias('a')->field('a.*,b.money_type_id as b_id,b.type as b_type')->join('left join y_money_type as b on a.money_type_id = b.money_type_id')->where($w)->order('t desc')->limit($p*$num,$num)->select();        foreach ($data as $k=>$v){            if($v['money_type_id'] == 7){                $data[$k]['symbol']='+';            }elseif($v['money_type_id'] == 5){                $data[$k]['symbol']='-';            }        }        //商家营业总额        $rental=R('Func/Business/rental',array($uid));        $rentalMoney=sprintf("%.2f",$rental);        echo json_encode(array('code'=>6501,'msg'=>'获取成功','data'=>$data,'pagesize'=>$count,'pagesum'=>$sum,'rental'=>$rentalMoney));die;    }    #商家 订单详情    public function selectOrder(){        $id=$_REQUEST['business_order_id'];        if (!$this->sign){            echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;        }        $array = array(            'business_order_id'=>$id,        );        $this->parem=array_merge($this->parem,$array);        $msg = R('Func/Func/getKey',array($this->parem));//返回加密        if ($this->sign !== $msg){            echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;        }        R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)        $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断        if (!$id){            echo json_encode(array('code'=>6502,'msg'=>'参数不全'));die;        }        $w['business_order_id']=$id;        $data =M('business_order')->field('business_order_id,business_id,user_id,goods_name,user_pay_supply_id,goods_type,pay_name,service_charge,pt_ordersn,timestamp,sh_ordersn,jy_status,js_status,money,pay_money,money_type_id,t,d_t,coupon_data_id,jifen_jilu_id,minus')->where($w)->find();        //付款用户名称        if ($data['user_id'] == '12' || $data['user_id'] == '13' || $data['user_id'] == '14' || $data['user_id'] == '15'){            $data['nick_name']=M('user')->where(array('user_id'=>$data['user_id']))->getField('nick_name');        }else{            $user_name=M('user')->where(array('user_id'=>$data['user_id']))->getField('nick_name');            $member =M('business_member')->where(array('business_id'=>$data['business_id'],'user_id'=>$data['user_id']))->find();           if ($member){  //存在当前商家会员表中            $data['nick_name']=$user_name.'-'.'店铺会员';           }else{               $data['nick_name']=$user_name.'-'.'普通用户';           }        }        if ($data['jifen_jilu_id']){         $data['jifen']=M('jifen_jilu')->where(array('jifen_jilu_id'=>$data['jifen_jilu_id']))->getField('num');        }else{            $data['jifen']='';        }        if ($data['coupon_data_id']){           $couponId=M('coupon_data')->where(array('coupon_data_id'=>$data['coupon_data_id']))->getField('coupon_id');           $data['coupon_money']=M('coupon')->where(array('coupon'=>$couponId))->getField('money');        }else{            $data['coupon_money']='';        }        if (!$data['minus']){           $data['minus']=0;        }        //支付方式        $data['fukuan']=M('user_pay_supply')->