        $page=1;        }                $pagenum=15;        $startnum=($page-1)*$pagenum;        $endnum=$page*$pagenum;                $limit=''.$startnum.','.$endnum.'';                $wherestore['user_id']=$user_id;                $businesslist=M('business')->where($wherestore)->limit($limit)->select();                for ($i = 0; $i < count($businesslist); $i++) {                        //php获取今日开始时间戳和结束时间戳            $beginToday=mktime(0,0,0,date('m'),date('d'),date('Y'));            $endToday=mktime(0,0,0,date('m'),date('d')+1,date('Y'))-1;                        $whereorder['business_id']=$businesslist[$i]['business_id'];            $whereorder['jy_status']=1;            $whereorder['_string']=" t between $beginToday and $endToday";             $todaymoney=M('business_order')->where($whereorder)->sum('pay_money');                        //php获取昨日起始时间戳和结束时间戳            $beginYesterday=mktime(0,0,0,date('m'),date('d')-1,date('Y'));            $endYesterday=mktime(0,0,0,date('m'),date('d'),date('Y'))-1;                        $whereyesorder['business_id']=$businesslist[$i]['business_id'];            $whereyesorder['jy_status']=1;             $whereyesorder['_string']=" t between $beginYesterday and $endYesterday";            $yesdaymoney=M('business_order')->where($whereyesorder)->sum('pay_money');            //             echo M('business_order')->getLastSql();            if(!$todaymoney) {                $todaymoney=0;            }            if(!$yesdaymoney) {                $yesdaymoney=0;            }                        $wherework['business_id']=$businesslist[$i]['business_id'];            $workcount=M('business_work')->where($wherework)->count();            if(!$workcount)            {                $workcount=0;            }                        $businesslist[$i]['thumb']=enThumb("./Uploads/", $businesslist[$i]['thumb']);            $businesslist[$i]['todaymoney']=$todaymoney;            $businesslist[$i]['yesdaymoney']=$yesdaymoney;            $businesslist[$i]['workcount']=$workcount;        }                if ($businesslist){            echo json_encode(array('code'=>200,'msg'=>'获取成功','businesslist'=>$businesslist));die;        }else        {            echo json_encode(array('code'=>201,'msg'=>'店铺门店数据为空'));die;        }            }          //收款码列表    public function codelist()    {                // 商家id        $business_id=$_REQUEST['business_id'];                        if (!$this->sign){         echo json_encode(array('code'=>10005,'msg'=>'加密字符串不存在'));die;         }                  $array = array(         'business_id'=>$business_id,         );                  $this->parem=array_merge($this->parem,$array);                  $msg = R('Func/Func/getKey',array($this->parem));//返回加密                  if ($this->sign !== $msg){         echo json_encode(array('code'=>10004,'msg'=>'禁止访问'));die;         }                  R('Func/Func/getTwoSign',array($this->sign)); //判断是否相同参数传第二次(如果是正常调用接口,参数不可能和sign相同)                  $_SESSION['last_sign'] = $this->sign;//把sign存入session 为作判断                  if (!$business_id){                  echo json_encode(array('code'=>6502,'msg'=>'参数不全'));die;                  }                  $page = $_REQUEST['page'];  //页数                 $wherecode['y_code.business_id']=$business_id;                if(!$page)        {            $page=1;        }                $pagenum=15;        $startnum=($page-1)*$pagenum;        $endnum=$page*$pagenum;                $limit=''.$startnum.','.$endnum.'';                $codelist=M('code')->where($wherecode)->JOIN('LEFT JOIN y_business on y_business.business_id=y_code.business_id')->field('y_code.*,y_business.shop_name,y_business.thumb')->limit($limit)->select();                       $url='https://'.$_SERVER['HTTP_HOST'].'/Wap/Collectionmoney/paycode/code/';                for ($i = 0; $i < count($codelist); $i++) {