<?php# 银钱包对接接口 - 微信api接口namespace Func\Controller;use Think\Controller;class WxapiController extends Controller{    public $wxconfig;    public function _initialize()    {//        $weid = isset($_REQUEST['weid'])?intval($_REQUEST['weid']):1;//        $weid = isset($_REQUEST['weid'])?intval($_REQUEST['weid']):2; // 切换为公众2        $this->wxconfig = M('wxconfig')->where(array('is_check'=>1))->find();//        dump($this->wxconfig);die;    }    // 自动处理服务器配置验证    public function getConfig()    {        import('Vendor.WechatSdk.Wechat');        $options = array( 			'token'=>$this->wxconfig['token'], //填写你设定的key			'encodingaeskey'=>$this->wxconfig['encodingaeskey'], //填写加密用的EncodingAESKey			'appid'=>$this->wxconfig['appid'], //填写高级调用功能的app id			'appsecret'=>$this->wxconfig['appsecret'], //填写高级调用功能的密钥            'debug'=>true  		);//        dump($options);        $weObj = new \Wechat($options);//        dump($this->$weObj);        return $weObj;    }  //获取自动回复    public function getText(){        $data = M('wx_text')->where(array('status'=>1))->select();//        dump($data);//        foreach ($data as $k=>$v){//            dump($v['id']);//        }        return $data;    }}